import React from 'react';
import { Input, Button } from 'semantic-ui-react';
import '../style/searchbar.css';
import logo from '../assets/youtube.png';

class Searchbar extends React.Component {
    handleChange = (event) => {
        this.setState({
            term: event.target.value
        });

    };
    handleSubmit = event => {
        event.preventDefault();
        this.props.handleFormSubmit(this.state.term);
        console.log(event);
    }

    render() {

        return (
            <>
            <div><img style={{width:'%', height:'100px',justifyContent:'center' }} src={logo} alt="youtube logo"></img></div>
            <div className='search-bar ui segment'>
                <div className='ui row' style={{display:'center'}}>
                    <form onSubmit={this.handleSubmit} className='container-form' style={{flexDirection: "row", flexWrap: 'no-wrap'}}>
                        <Input onChange={this.handleChange} name='video-search' type="text" placeholder="Search.." style={{textAlign:"center", minWidth: '70%',maxWidth:'75%', height:'10%', flex: 3, justifyContent:'center', borderRadius: '300px', marginRight: '15px'}}/>
                        <Button content="Search" style={{fontSize: "4rem", textAlign:"center", minWidth:'20%', maxWidth:'20%', height:'50px',justifyContent:'center', flex: '1'}}/>
                    </form>
                </div>
            </div>
            </>
        )
    }
}
export default Searchbar;
