import React from 'react';
import { Input, Button } from 'semantic-ui-react';
import '../style/searchbar.css';


class Searchbar extends React.Component {
    handleChange = (event) => {
        this.setState({
            term: event.target.value
        });

    };
    handleSubmit = event => {
        event.preventDefault();
        this.props.handleFormSubmit(this.state.term);
        console.log(event);
    }

    render() {

        return (
            <>
            <div className=''>
                <div className='' style={{display:'center'}}>
                    <form onSubmit={this.handleSubmit} className='' style={{flexDirection: "row", flexWrap: 'no-wrap'}}>
                        <input onChange={this.handleChange} name='video-search' type="text" placeholder="Search.." style={{textAlign:"center", minWidth: '70%',maxWidth:'75%', height:'10%', flex: 3, justifyContent:'center', borderRadius: '300px', marginRight: '15px'}}/>
                        <Button className=""content="Search" style={{fontSize: "1.2rem", textAlign:"center", minWidth:'20%', maxWidth:'20%', height:'50px', flex: '1'}}/>
                    </form>
                </div>
            </div>
            </>
        )
    }
}
export default Searchbar;
