import React from 'react';
import VideoList from './VideoList';
import '../style/videodetail.css';
import { useLocation } from 'react-router';
import { Link } from 'react-router-dom';

const MoreDetails= ({handleVideoDetails, handleVideoSelect, video, videos}) => {
    const videoSrc = `https://www.youtube.com/embed/${video.id.videoId}`;
    const location = useLocation();
    return (
        <>
        <div className = "details-not-search"></div>
        <div className="videos">
            <div className="video-repro">
                <h2 className="title">{video.snippet.title}</h2>
                <p className="description">{video.snippet.description}</p>
                <div className="video">
                    <iframe src={videoSrc} allowFullScreen title="Video player" className="video-player" />
                </div>
                <div className="details">
                    {videos.length === 0 ? <div></div> :
                    <button onClick={ () => handleVideoDetails()} className="details-button">Back </button>}
                </div>
            </div>
            <VideoList handleVideoSelect={handleVideoSelect} videos={videos}/>
        </div>
        </>
    )
}
export default MoreDetails;
