import React from 'react';
import { Input, Button } from 'semantic-ui-react'
import '../style/searchbar.css';

class Searchbar extends React.Component {
    handleChange = (event) => {
        this.setState({
            term: event.target.value
        });

    };
    handleSubmit = event => {
        this.props.handleFormSubmit(this.state.term);
    }

    render() {

        return (
            <>
            <div style={{width:'200px', height:'10px',justifyContent:'center'}}></div>
            <div className='search-bar ui segment'>
                <div className='ui row' style={{display:'center'}}>
                    <form onSubmit={this.handleSubmit} className='container-form' style={{flexDirection: "row"}}>
                        <Input onChange={this.handleChange} name='video-search' type="text" placeholder="Search.." style={{textAlign:"center", minWidth: '75%',maxWidth:'75%', height:'10%', flex: 3, justifyContent:'center', borderRadius: '300px', marginRight: '20px'}}/>
                        <Button content="Search" style={{textAlign:"center", minWidth:'20%', maxWidth:'10%', height:'50px',justifyContent:'center', flex: 1}}/>
                    </form>
                </div>
            </div>
            </>
        )
    }
}
export default Searchbar;
