import React from 'react';
import SearchBar from './Searchbar';
import youtube from '../apis/youtube';
import VideoList from './VideoList';
import VideoDetail from './VideoDetail';
import logo from '../assets/youTubeLogoGif.gif';
import "../style/app.css"


class App extends React.Component {
    state = {
        videos: [],
        selectedVideo: null,
        details : false
    }
    handleSubmit = async (termFromSearchBar) => {
        const response = await youtube.get('/search', {
            params: {
                q: termFromSearchBar
            }
        })

        this.setState({
            videos: response.data.items
        })
        console.log("this is resp",response);
    };
    handleVideoSelect = (video) => {
        this.setState({selectedVideo: video})
    }
    handleVideoDetails = () => {

        this.state.details = true
        console.log(this.state.details);
    }

    render() {
        return (

            <div className='parent'>
                <div className= "header">
                    <img style={{height:'100px',justifyContent:'center'}} src={logo} alt="youtube logo" centered/>
                    </div>
                <SearchBar handleFormSubmit={this.handleSubmit}/>
                <div className="videos">
                    <div className="video-repro">
                        <VideoDetail video={this.state.videos[0]}/>
                        <div className="details">
                            {this.state.videos.length == 0 ? <div></div> :
                             <button onClick={ () => handleVideoDetails()} className="details-button">More details </button>}

                        </div>
                    </div>
                    <VideoList handleVideoSelect={this.handleVideoSelect} videos={this.state.videos.slice(1)}/>
                </div>
            </div>
        )
    }
}

export default App;
