import React from 'react';

class Searchbar extends React.Component {
    handleChange = (event) => {
        this.setState({
            term: event.target.value
        });

    };
    handleSubmit = event => {
        event.preventDefault();
        this.props.handleFormSubmit(this.state.term);
    }

    render() {

        return (
            <>
            <h2  style={{textAlign:"center"}}><img style={{width:'200px', height:'100px',justifyContent:'center'}} src='https://www.thatitguy.net/wp-content/uploads/2018/08/1280px-Logo_of_YouTube_2015-2017.svg.png' alt="youtube logo"></img></h2>
            <div className='search-bar ui segment' style={{display:"flex"}}>
                <form onSubmit={this.handleSubmit} className='ui form' style={{flex: 1}}>
                    <div className='field' style={{flexDirection:"row"}}>
                        <input onChange={this.handleChange} name='video-search' type="text" placeholder="Search.."/>
                        <div><button style={{textAlign:"center", width:'200px', height:'50px'}}></button></div>
                    </div>
                </form>
            </div>
            </>
        )
    }
}
export default Searchbar;
