import React, {useRef, useEffect} from 'react';
import SearchBar from './Searchbar';
import youtube from '../apis/youtube';
import VideoList from './VideoList';
import VideoDetail from './VideoDetail';
import logo from '../assets/youTubeLogoGif.gif';
import "../style/app.css"



class App extends React.Component {
    state = {
        videos: [],
        selectedVideo: null,
        details : false
    }
    handleSubmit = async (termFromSearchBar) => {
        const response = await youtube.get('/search', {
            params: {
                q: termFromSearchBar
            }
        })

        this.setState({
            videos: response.data.items
        })
        console.log("this is resp",response);
    };
    handleVideoSelect = (video) => {
        this.setState({selectedVideo: video})
        if (this.state.selectedVideo && this.state.videos.length > 0) {

            let selectedVideoId = this.state.selectedVideo.id.videoId;
            this.state.videos.forEach(function (element, index, object ) {
                if ( selectedVideoId === element.id.videoId) {
                    object.splice(index, 1);
                    this.state.videos.push(element);
                    return;
                }
            });
        }
    }
    handleVideoDetails = () => {
        this.setState({details: !this.state.details})
        console.log(this.state.details);
    }


    render() {
        return (

            <div className='parent'>
                <div className= "header">
                    <img style={{height:'100px',justifyContent:'center'}} src={logo} alt="youtube logo" centered/>
                    </div>
                { this.state.details ? ( 
                    <>
                    <div className = "details-not-search"></div>
                    <div className="videos">
                        <div className="video-repro">
                            {this.state.selectedVideo ? (
                            <VideoDetail video={this.state.videos[0]}  details ={this.state.details}/>
                            ) : (
                            <VideoDetail video={this.state.selectedVideo}  details ={this.state.details}/>
                            )}
                            <div className="details">
                                {this.state.videos.length === 0 ? <div></div> :
                                <button onClick={ () => this.handleVideoDetails()} className="details-button">Back </button>}

                            </div>
                        </div>
                        <VideoList handleVideoSelect={this.handleVideoSelect} videos={this.state.videos.slice(1)}/>
                    </div>
                    </>

                    ) : (

                    <>
                    <SearchBar handleFormSubmit={this.handleSubmit}/>
                    <div className="videos">
                        <div className="video-repro">
                            {this.state.selectedVideo ? (
                            <VideoDetail video={this.state.videos[0]}  details ={this.state.details}/>
                            ) : (
                            <VideoDetail video={this.state.selectedVideo}  details ={this.state.details}/>
                            )}
                            <div className="details">
                                {this.state.videos.length === 0 ? <div></div> :
                                <button onClick={ () => this.handleVideoDetails()} className="details-button">More details </button>}

                            </div>
                        </div>
                        <VideoList handleVideoSelect={this.handleVideoSelect} videos={this.state.videos.slice(1)}/>
                    </div>
                    </>
                    )}
            </div>
        )
    }
}

export default App;
