import React from 'react';
import { Input, Button } from 'semantic-ui-react'

class Searchbar extends React.Component {
    handleChange = (event) => {
        this.setState({
            term: event.target.value
        });

    };
    handleSubmit = event => {
        event.preventDefault();
        this.props.handleFormSubmit(this.state.term);
    }

    render() {

        return (
            <>
            <div style={{width:'200px', height:'10px',justifyContent:'center'}} src='https://www.thatitguy.net/wp-content/uploads/2018/08/1280px-Logo_of_YouTube_2015-2017.svg.png' alt="youtube logo"></div>
                <div className='ui row'>
                    <form onSubmit={this.handleSubmit} className='ui form'>
                        <Input onChange={this.handleChange} name='video-search' type="text" placeholder="Search.."/>
                        <Button content="Search" style={{textAlign:"center", width:'200px', height:'50px',justifyContent:'center'}}/>
                    </form>
                </div>
            </>
        )
    }
}
export default Searchbar;
